mapper.update:

	UPDATE tbl_book_loan SET return_sche=TO_DATE(return_sche,'yyyy-mm-dd HH24:mi:ss')+3,								
							loan_state=#{loan_state,jdbcType=INTEGER} 
		WHERE b_no=#{b_no,jdbcType=VARCHAR} AND user_no=#{user_no,jdbcType=VARCHAR}

mapper.select:
SELECT * FROM tbl_lib_seat	
	WHERE seat_no = #{seat_no} 
	AND rdRoom_no = #{rdRoom_no}
	
		AND user_no = #{user_no}

SELECT seat_no, seat.user_no, rdRoom_no, seat_st_dt, seat_end_dt, seat_usetime, seat_state, st.st_name user_name FROM tbl_lib_seat seat, tbl_student st WHERE seat.user_no = st.st_no
AND seat_no = #{seat_no} AND rdRoom_no = #{rdRoom_no}
AND user_no = #{user_no}

SELECT * FROM tbl_book_loan
	WHERE user_no = #{user_no}
	ORDER BY loan_dt DESC
