mapper.select:
SELECT COUNT(*) FROM tbl_book
	
		
			
				b_title LIKE #{title}
				
					AND b_author LIKE #{author}						
				
			
			
				
					b_author LIKE #{author}



SELECT * FROM(SELECT b_no,b_isbn,b_global,b_category,b_title,b_price,b_author,b_publish,
				b_date,b_img,b_intoDate,b_state_fl,rownum rnum
		FROM (SELECT * FROM tbl_book ORDER BY b_global ASC, b_category ASC, b_date ASC, b_no ASC)
	)WHERE rnum >= #{start} AND rnum <= #{end}

mapper.update:
UPDATE tbl_book SET b_global=#{b_global,jdbcType=INTEGER},b_category=#{b_category,jdbcType=INTEGER},
					b_title=#{b_title,jdbcType=VARCHAR},b_price=#{b_price,jdbcType=INTEGER},
					b_author=#{b_author,jdbcType=VARCHAR},b_publish=#{b_publish,jdbcType=VARCHAR},
					b_date=#{b_date,jdbcType=VARCHAR},b_img=#{b_img,jdbcType=VARCHAR},
					b_state_fl=#{b_state_fl,jdbcType=INTEGER} 
	WHERE b_no=#{b_no}

mapper.select:
SELECT * FROM tbl_book_loan
	
		
			
				b_no = #{b_no}	
			
									
				TO_CHAR(loan_dt,'YYYY/MM/DD') = TO_CHAR(sysdate,'YYYY/MM/DD')
				
					AND user_no = #{user_no}
				
			
				
				
	
	ORDER BY loan_dt

SELECT * FROM tbl_book_loan 
	
		b_no = #{b_no}
		
		
			AND user_no=#{user_no}
		
		
			AND loan_state IN
			
				#{state}


SELECT * FROM(SELECT b_no,b_isbn,b_global,b_category,b_title,b_price,b_author,b_publish,
				b_date,b_img,b_intoDate,b_state_fl,rownum rnum
		FROM (SELECT * FROM tbl_book 
		
			
				
					b_title LIKE #{title}
					
						AND b_author LIKE #{author}						
					
				
				
					
						b_author LIKE #{author}						
											
															
							
						
		ORDER BY b_global ASC, b_category ASC, b_date ASC, b_no ASC)
	)

	WHERE rnum >= #{start} AND rnum <= #{end}

mapper.update:
UPDATE tbl_lib_seat 
	SET user_no=#{user_no,jdbcType=VARCHAR},seat_st_dt=sysdate,
		seat_end_dt=sysdate+10/1440,seat_usetime=#{seat_usetime,jdbcType=VARCHAR},
		seat_state=#{seat_state,jdbcType=INTEGER} 
	WHERE seat_no=#{seat_no} AND rdRoom_no=#{rdRoom_no}
